import "./modal.css";
import React, { useState, useEffect } from "react";
import PodcastCard from "../PodcastCard/PodcastRecommendationCard";
import { getPodcastsByGenreAndMaxLength } from "../../lib/podcastApi";
import SadLemon from "../../assets/SadLemon.PNG"

const steps = [15, 30, 45, 60, 75, 90, 105, 120];

const RecommendationModal = ({ searchValues, onClose, newSearch }) => {
    const [skipCount, setSkipCount] = useState(0);
    const [loading, setLoading] = useState(true);
    const [podcastRecommendedData, setPodcastRecommendedData] = useState([]);

    async function getPodcasts() {
        const podcasts = await getPodcastsByGenreAndMaxLength(searchValues.genres, steps[searchValues.time]);
        setPodcastRecommendedData(podcasts)
        setLoading(false)
    }

    useEffect(() => {
        getPodcasts();
    }, []);

    const currentPodcast = podcastRecommendedData[skipCount];

    return (
        <div className="main-modal-window" tabIndex={-1}>
            <div className="main-modal-content">
                {/* Close Icon */}
                <div className="close-container">
                    <svg
                        onClick={onClose}
                        version="1.1"
                        className="close-icon"
                        x="0px"
                        y="0px"
                        viewBox="0 0 47.971 47.971"
                    >
                        <path d="M28.228,23.986L47.092,5.122c1.172-1.171,1.172-3.071,0-4.242c-1.172-1.172-3.07-1.172-4.242,0L23.986,19.744L5.121,0.88c-1.172-1.172-3.07-1.172-4.242,0c-1.172,1.171-1.172,3.071,0,4.242l18.865,18.864L0.879,42.85c-1.172,1.171-1.172,3.071,0,4.242C1.465,47.677,2.233,47.97,3,47.97s1.535-0.293,2.121-0.879l18.865-18.864L42.85,47.091c0.586,0.586,1.354,0.879,2.121,0.879s1.535-0.293,2.121-0.879c1.172-1.171,1.172-3.071,0-4.242L28.228,23.986z" />
                    </svg>
                </div>

                {/* Header space in Modal */}
                <div className="main-modal-header"></div>

                {/* Main content of modal */}
                <div className="main-modal-body">
                    <div className="main-modal-title">
                        {!currentPodcast && !loading ? "You've squeezed all the recommendations out of this search:" :
                            "Our Recommendation, freshly squeezed for you:"
                        }
                    </div>

                    <div className="modal-recommendation-container">
                        {loading && (
                            <div>Loading podcasts</div>
                        )}
                        {currentPodcast && (
                            <PodcastCard
                                onSkip={setSkipCount}
                                recommendationValue={currentPodcast}
                            />
                        )}
                        {!currentPodcast && !loading && (
                            <div>
                                <img src={SadLemon} alt="SadLemon" />
                                <div className="recommendation-buttons">
                                    <button onClick={() => setSkipCount(0)} className="repeat-button">Repeat Recommendations</button>
                                    <button onClick={() => newSearch(1)} className="repeat-button">New Search</button>
                                </div>
                            </div>
                        )}
                    </div>
                    <div className="main-modal-footer"></div>

                </div>
            </div>
        </div>
    );
};

export default RecommendationModal;
