import "./modal.css";
import { useState } from "react";
import LoginModal from "./LoginModal";
import AccountCreateModal from "./AccountCreationModal";
import PasswordResetInitialModal from "./PasswordResetInitialModal";
import PasswordResetEmailSentModal from "./PasswordResetEmailSentModal";
import PasswordResetConfirmModal from "./PasswordResetConfirmModal";

// screen to link all Login Modals together

function LoginJourney(props) {

    const [screen, setScreen] = useState('login')

    function closeModal() {
        setScreen('login')
        props.onClose();
    }

    if (!props.open)
        return null

    return (
        <>
            {screen === 'login' && <LoginModal setScreen={setScreen} onClose={closeModal} />}
            {screen === 'account' && <AccountCreateModal setScreen={setScreen} />}
            {screen === 'resetInitial' && <PasswordResetInitialModal setScreen={setScreen} onClose={closeModal} />}
            {screen === 'resetEmail' && <PasswordResetEmailSentModal setScreen={setScreen} onClose={closeModal} />}
            {screen === 'resetConf' && <PasswordResetConfirmModal setScreen={setScreen} onClose={closeModal} />}
        </>
    )
}

export default LoginJourney;
