import React, { useState } from "react";
import "./Modal.css";
import ToggleSwitch from "../ToggleSwitch/ToggleSwitch";
import GenreButton from "../GenreButton/GenreButton";

const genreButtons = [
    { id: 88, title: "Health" },
    { id: 134, title: "Music" },
    { id: 135, title: "True Crime" },
    { id: 133, title: "Comedy" },
    { id: 68, title: "TV / Film" },
    { id: 111, title: "Education" },
    { id: 107, title: "Science" },
    { id: 99, title: "News" },
    { id: 168, title: "Fiction" }
];

function shouldToggleGenre(currentSelectedGenres, newId) {
    if (currentSelectedGenres.includes(newId)) {
        return currentSelectedGenres.filter(genre => genre !== newId)
    }
    if (currentSelectedGenres.length >= 3) {
        return currentSelectedGenres;
    }
    return [...currentSelectedGenres, newId]
}

const GenreModal = ({ nextScreen, onClose }) => {
    const [genres, setGenres] = useState([])
    const [isAnyCategory, setIsAnyCategory] = useState(false);

    const onChangeIsAnyCategory = () => setIsAnyCategory(!isAnyCategory);

    // Function setup to pass Genre ID's for selected buttons over to API call
    function toggleGenre(genreId) {
        setGenres((currentSelectedGenres) => shouldToggleGenre(currentSelectedGenres, genreId))
    }

    // Handling communication between modal screens
    function handleSubmit() {
        if (isAnyCategory) {
            return nextScreen({ genres: [] })
        }
        nextScreen({ genres })
    }

    return (
        // Main Modal Window CSS to implement styling to background page
        <div className="main-modal-window" tabIndex={-1}>
            {/* Style for the modal's content */}
            <div className="main-modal-content">
                {/* Close Icon */}
                <div className="close-container">
                    <svg onClick={onClose} version="1.1" className="close-icon" x="0px" y="0px" viewBox="0 0 47.971 47.971"><path d="M28.228,23.986L47.092,5.122c1.172-1.171,1.172-3.071,0-4.242c-1.172-1.172-3.07-1.172-4.242,0L23.986,19.744L5.121,0.88c-1.172-1.172-3.07-1.172-4.242,0c-1.172,1.171-1.172,3.071,0,4.242l18.865,18.864L0.879,42.85c-1.172,1.171-1.172,3.071,0,4.242C1.465,47.677,2.233,47.97,3,47.97s1.535-0.293,2.121-0.879l18.865-18.864L42.85,47.091c0.586,0.586,1.354,0.879,2.121,0.879s1.535-0.293,2.121-0.879c1.172-1.171,1.172-3.071,0-4.242L28.228,23.986z" /></svg>
                </div>

                {/* Header space in Modal */}
                <div className="main-modal-header">

                </div>

                {/* Main content of modal for user interaction */}
                <div className="main-modal-body">

                    <h3 className="main-modal-title">Select your preferred category:</h3>

                    {/* If toggled, all buttons are disabled */}
                    <div className='genre-toggle-container'>
                        <ToggleSwitch
                            label="Any category"
                            onToggle={onChangeIsAnyCategory}
                            value={isAnyCategory}
                        />
                    </div>

                    <p id='choose-genre'>Or you can choose up to 3 categories:</p>

                    {/* Genre buttons which then feed into the Podcast Recommendation Screen */}
                    <div className='genre-button-container'>
                        {genreButtons.map(({ id, title }) => (
                            <GenreButton
                                key={id}
                                onClick={toggleGenre}
                                id={id}
                                title={title}
                                disabled={isAnyCategory}
                                isActive={genres.includes(id)}
                                maxSelected={genres.length >= 3}
                            />
                        ))}
                    </div>

                    {/* Button which leads user from Genre to Time selection modal */}
                    <button type="submit" onClick={handleSubmit} className="button">Next</button>

                </div>
                <div className="main-modal-footer"></div>
            </div>
        </div>
    )
}
export default GenreModal;
