import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import podcastcover from '../../assets/podcastcover1.jpg';
import { useState } from "react";
import '../CarouselHome/Carousel.css';

function PodcastCard(props){
    return (
            <Card>
                <Card.Body className = "podcast-card-body">
                    <div className="podcast-card-image">
                        <Card.Img variant="top" src={podcastcover} />
                    </div>
                    <Card.Title className ="podcast-card-title">Podcast Title</Card.Title>
                    <Card.Subtitle className = "podcast-card-subtitle">Author</Card.Subtitle>
                    <Card.Text className = "podcast-card-text">
                        <p className = "podcast-card-text">Description text for the podcast. </p>
                    </Card.Text>
                    <Button className = "card-listen-button" variant="primary">Listen</Button>
                    <Button className = "card-follow-button" variant="primary">Follow</Button>
                </Card.Body>
            </Card>
    )
};

export default PodcastCard;
