import './PodcastCard.css';
import React, { useEffect, useState } from 'react';
import ReactAudioPlayer from "react-audio-player";

export const CarouselItem = ({children}) => {

    return (
        <div className='podcast-carousel-item'> 
            {children}
        </div>
    );

};

const PodcastCarouselCard = (props) => {

    console.log(props.podcastid)

    return (
        <div>
            <CarouselItem>
                    <div className = "podcast-card-body">
                        <div className="podcast-card-image">
                            <img src={props.podcastimage} />
                        </div>

                        <div className='podcast-card-content'>
                            <div className='podcast-card-title'>Title: { props.podcasttitle }</div>
                            <div className='podcast-card-subtitle'>Host: { props.podcastauthor }</div>

                            <div className = "podcast-card-text">
                                <p> { props.podcastdescription }</p>
                            </div>
                        </div>

                        <div className='button-container'>
                            <button className="card-listen-button" id={props.podcastid} variant="primary">Listen</button>
                        </div>
                    </div>
            </CarouselItem>        
        </div>
)
};

export default PodcastCarouselCard;
