import "./modal.css";
import { useState } from "react";
import LoginModal from "./LoginModal";
import AccountCreateModal from "./accountCreationModal";

// screen to link all Login Modals together
// TODO: add Password Reset screens

function LoginJourney(props){
    const [screen,setScreen] = useState(1)
    const [LoginJourneyValue, setLoginJourneyValues] = useState({})

    function nextScreen(screenValue){
        setScreen(current => current +1)
        setLoginJourneyValues(previous => ({...previous, ...screenValue}))
    }

    function closeModal(){
        setScreen(1);
        props.onClose();
    }

    if (!props.open)
        return null

    return(
    <>
    {screen===1 &&<LoginModal nextScreen={nextScreen} onClose={closeModal}/>}
    {screen===2 &&<AccountCreateModal nextScreen={nextScreen} onClose={closeModal}/>}
    {/* {screen===3 &&<RecommendationModal searchValues={journeyValue} nextScreen={nextScreen} onClose={closeModal}/>} */}

    </>
)}

export default LoginJourney;
