import React, { useState } from "react";
import "./Modal.css";
import ToggleSwitch from "../ToggleSwitch/ToggleSwitch";
import LengthSlider from "../LengthSlider/LengthSlider"
import Step from "../LengthSlider/TimeIncrements";

const TimeModal = ({ onClose, nextScreen }) => {
    const [sliderValue, setSliderValue] = useState(0)
    const [isAnyLength, setIsAnyLength] = useState(false)

    const onChangeIsAnyLength = () => setIsAnyLength(!isAnyLength);

    function handleSubmit() {
        if (isAnyLength) {
            // sets to max podcast length of 2 hours
            return nextScreen({ time: 7 })
        }
        nextScreen({ time: sliderValue })
    }

    function handleSliderChange(value) {
        setSliderValue(value)
    }

    return (
        // Main Modal Window CSS to implement styling to background page
        <div className="main-modal-window" tabIndex={-1}>
            {/* Style for the modal's content */}
            <div className="main-modal-content">
                {/* Close Icon */}
                <div className="close-container">
                    <svg onClick={onClose} version="1.1" className="close-icon" x="0px" y="0px" viewBox="0 0 47.971 47.971"><path d="M28.228,23.986L47.092,5.122c1.172-1.171,1.172-3.071,0-4.242c-1.172-1.172-3.07-1.172-4.242,0L23.986,19.744L5.121,0.88c-1.172-1.172-3.07-1.172-4.242,0c-1.172,1.171-1.172,3.071,0,4.242l18.865,18.864L0.879,42.85c-1.172,1.171-1.172,3.071,0,4.242C1.465,47.677,2.233,47.97,3,47.97s1.535-0.293,2.121-0.879l18.865-18.864L42.85,47.091c0.586,0.586,1.354,0.879,2.121,0.879s1.535-0.293,2.121-0.879c1.172-1.171,1.172-3.071,0-4.242L28.228,23.986z" /></svg>
                </div>
                {/* Header space in Modal */}
                <div className="main-modal-header"></div>

                {/* Main content of modal for user interaction */}
                <div className="main-modal-body">

                    <div className="main-modal-title">
                        Select your preferred length:
                    </div>

                    <div className='toggle-container'>
                        <ToggleSwitch
                            label="Any length"
                            className='genre-toggle'
                            onToggle={onChangeIsAnyLength}
                            value={isAnyLength}

                        />
                    </div>
                    <p id='choose-length'>Or choose how much time you have:</p>
                    {/* Slider to allow users to select time input for recommendation */}
                    <div className='time-slider-container'>
                        <LengthSlider value={sliderValue} onChange={handleSliderChange} disabled={isAnyLength} />
                    </div>

                    {/* Container which returns time value according to position of slider */}
                    <div className="value-slider-container">
                        <Step currentIndex={sliderValue} isAnyLength={isAnyLength} />
                    </div>

                    {/* Submit button which then leads user to Podcast Recommendation */}
                    <button type="submit" onClick={handleSubmit} className="button">Submit</button>

                </div>

                <div className="main-modal-footer"></div>


            </div>

        </div>
    )
}

export default TimeModal;
