import Mascot from "../../assets/UploadPhotoPlaceholder.png";
import BaseModal from "./BaseModal";

const PhotoUploadModal = ({ onClose }) => {
    return (
        <BaseModal onClose={onClose} className="main-modal-body">
            <h3 className="centred-modal-title">Upload your Avatar here!</h3>
            <img src={Mascot} title="Our Mascot: Kobi" alt="Close up of nose of a black and white dog, who is our Mascot" />
            <input type="file" className="image-input-field" />
            <button className="button" onClick={onClose}>
                Upload!
            </button>
        </BaseModal>
    )
}
export default PhotoUploadModal;
