import React from "react";
import { useState } from "react";
import HeroHome from "../../components/HeroHome/HeroHome";
import FindPodcastButton from "../../components/FindPodcastButton/FindPodcastButton";
import PodcastJourney from "../../components/Modal/PodcastJourney";
import LoginModal from "../../components/Modal/loginModal"
import AccountCreateModal from "../../components/Modal/accountCreationModal";
import PodcastCarousel, { CarouselItem } from "../../components/CarouselHome/PodcastCarousel";
import PodcastCard from "../../components/PodcastCard/PodcastCard";

// import AccountCreateModal from "../../components/Modal/accountCreationModal";

function Home(){
    const[showPodcastJourney,setshowPodcastJourney]=useState(false)

    function openPodcastJourney(){
        setshowPodcastJourney(true);
    }
    function closeModal(){
        setshowPodcastJourney(false);
    }

    return(
        <div>
            <HeroHome/>
            <FindPodcastButton onClick={openPodcastJourney}/>
            <PodcastJourney onClose={closeModal} open={showPodcastJourney}/>
            {/* <LoginModal show={showModal} handleClose={closeModal}/> */}
            {/* <AccountCreateModal onClose={() => setshowModal(false)} showModal={showModal}/> */}
            <PodcastCarousel>
                <CarouselItem>
                        <PodcastCard/>
                </CarouselItem>
                <CarouselItem>
                        <PodcastCard/>
                </CarouselItem>
                <CarouselItem>
                        <PodcastCard/>
                </CarouselItem>
                <CarouselItem>
                        <PodcastCard/>
                </CarouselItem>
                <CarouselItem>
                        <PodcastCard/>
                </CarouselItem>
                <CarouselItem>
                        <PodcastCard/>
                </CarouselItem>
            </PodcastCarousel>
            {/* <AccountCreateModal onClose={() => setshowModal(false)} showModal={showModal}/> */}

        </div>
    );
};

export default Home;
