import React, { useEffect, useState } from 'react';
import { useSwipeable } from 'react-swipeable';
import './UserPageFeatures.css';
import PodcastCarousel from "../CarouselHome/Carousel";
import PodcastCarouselCard, { CarouselItem } from '../UserPageFeatures/UserPodcastCarouselCard'
import podcasts from '../../api/68-podcasts-genre.json';

function SavedForLater () {
    return (

        <div className='saved-later-container'>

            <div className='saved-title'>
                <h1 className='user-titles'>Saved for Later</h1>
                <p className='user-subtitles'>You wanted to make sure you returned to these...</p>
            </div>

            <div className='carouselcontainer'>

                <PodcastCarousel >
                    <CarouselItem>
                <PodcastCarouselCard podcastimage={podcasts.podcasts[1].thumbnail}
                podcasttitle={<tr><td>{podcasts.podcasts[1].title}</td></tr>}
                podcastauthor={<tr><td>{podcasts.podcasts[1].publisher}</td></tr>}
                podcastdescription={<tr><td>{podcasts.podcasts[1].description}</td></tr>}/>
                </CarouselItem>

                <CarouselItem>
                <PodcastCarouselCard podcastimage={podcasts.podcasts[0].thumbnail}
                podcasttitle={<tr><td>{podcasts.podcasts[0].title}</td></tr>}
                podcastauthor={<tr><td>{podcasts.podcasts[0].publisher}</td></tr>}
                podcastdescription={<tr><td>{podcasts.podcasts[0].description}</td></tr>}/>
                </CarouselItem>


                {/* <CarouselItem>
                <PodcastCarouselCard podcastimage={podcasts.podcasts[3].thumbnail}
                podcasttitle={<tr><td>{podcasts.podcasts[3].title}</td></tr>}
                podcastauthor={<tr><td>{podcasts.podcasts[3].publisher}</td></tr>}
                podcastdescription={<tr><td>{podcasts.podcasts[3].description}</td></tr>}/>
                </CarouselItem>

                <CarouselItem>
                <PodcastCarouselCard podcastimage={podcasts.podcasts[4].thumbnail}
                podcasttitle={<tr><td>{podcasts.podcasts[4].title}</td></tr>}
                podcastauthor={<tr><td>{podcasts.podcasts[4].publisher}</td></tr>}
                podcastdescription={<tr><td>{podcasts.podcasts[4].description}</td></tr>}/>
                </CarouselItem> */}
                </PodcastCarousel>
            </div>

        </div>
    );
}

export default SavedForLater;
