import React from "react";
import "../../components/HeroListen/HeroListen.css";
import HeroListen from "../../components/HeroListen/HeroListen";
import PodcastCarousel from "../../components/CarouselHome/Carousel";
import PodcastCarouselCard, { CarouselItem } from "../../components/PodcastCard/PodcastCarouselCard";
import { useParams } from "react-router-dom";
import { useEffect, useState } from "react";
import { getPodcast } from "../../lib/podcastApi";
// import CustomComments from "../../components/Comments/CustomComponents";


function Listen() {
    const [podcast, setPodcast] = useState();
    let { id } = useParams();

    async function getPodcastByID() {
        const podcastResult = await getPodcast(id)
        setPodcast(podcastResult)
    }

    useEffect(() => {
        getPodcastByID()
    }, []);

    return (
        <div>
            {podcast && <HeroListen podcast={podcast} />}
            <div className='carousel-title'>
                And on that topic..
            </div>
            <div>
                <p className='carousel-subtitle'>Similar podcasts you may enjoy</p></div>
            <PodcastCarousel>
                <CarouselItem>
                    <PodcastCarouselCard />
                </CarouselItem>
                <CarouselItem>
                    <PodcastCarouselCard />
                </CarouselItem>
                <CarouselItem>
                    <PodcastCarouselCard />
                </CarouselItem>
                <CarouselItem>
                    <PodcastCarouselCard />
                </CarouselItem>
            </PodcastCarousel>
            {/* <CustomComments/> */}
        </div>
    )
}


export default Listen;
