import React, { useEffect, useState } from 'react';
import { useSwipeable } from 'react-swipeable';
import './UserPageFeatures.css';
import PodcastCarousel from "../CarouselHome/Carousel";
import PodcastCarouselCard, { CarouselItem } from '../UserPageFeatures/UserPodcastCarouselCard';
import podcasts from '../../api/68-podcasts-genre.json';

function MyFavourites () {
    return (

        <div className='saved-later-container'>

            <div className='saved-title'>
                <h1 className='user-titles'>My Favourites</h1>
                <p className='user-subtitles'>Of all the random picks, you loved these the best...</p>
            </div>

            <div>
                <PodcastCarousel>
                <CarouselItem>
                <PodcastCarouselCard podcastimage={podcasts.podcasts[17].thumbnail}
                podcasttitle={<tr><td>{podcasts.podcasts[17].title}</td></tr>}
                podcastauthor={<tr><td>{podcasts.podcasts[17].publisher}</td></tr>}
                podcastdescription={<tr><td>{podcasts.podcasts[17].description}</td></tr>}/>
                </CarouselItem>

                <CarouselItem>
                <PodcastCarouselCard podcastimage={podcasts.podcasts[16].thumbnail}
                podcasttitle={<tr><td>{podcasts.podcasts[16].title}</td></tr>}
                podcastauthor={<tr><td>{podcasts.podcasts[16].publisher}</td></tr>}
                podcastdescription={<tr><td>{podcasts.podcasts[16].description}</td></tr>}/>
                </CarouselItem>
                </PodcastCarousel>
            </div>

        </div>
    );
}

export default MyFavourites;
