import React from "react";
import "../../components/HeroListen/HeroListen.css";
import HeroListen from "../../components/HeroListen/HeroListen";
import PodcastCarousel from "../../components/Carousel/Carousel";
import PodcastCarouselCard, { CarouselItem } from "../../components/PodcastCards/PodcastCarouselCard";
import { useParams } from "react-router-dom";
import { useEffect, useState } from "react";
import { getPodcast } from "../../lib/podcastApi";
import podcasts from '../../api/mock-api.json';
// import CustomComments from "../../components/Comments/CustomComponents";


function Listen() {
    const [podcast, setPodcast] = useState();
    let { id } = useParams();

    async function getPodcastByID() {
        const podcastResult = await getPodcast(id)
        setPodcast(podcastResult)
    }

    useEffect(() => {
        getPodcastByID()
    }, []);

    return (
        <div>
            {podcast && <HeroListen podcast={podcast} />}
            <div className='carousel-title'>
                And on that topic..
            </div>
            <div>
                <p className='carousel-subtitle'>
                    Similar podcasts you may enjoy
                </p>
            </div>

            <PodcastCarousel>
                <CarouselItem>
                        <PodcastCarouselCard podcastimage={podcasts.podcasts[5].thumbnail}
                        podcasttitle={podcasts.podcasts[5].title}
                        podcastauthor={podcasts.podcasts[5].publisher}
                        podcastdescription={podcasts.podcasts[5].description}
                        podcastid={podcasts.podcasts[5].id}/>
                    </CarouselItem>

                    <CarouselItem>
                        <PodcastCarouselCard podcastimage={podcasts.podcasts[6].thumbnail}
                        podcasttitle={podcasts.podcasts[6].title}
                        podcastauthor={podcasts.podcasts[6].publisher}
                        podcastdescription={podcasts.podcasts[6].description}
                        podcastid={podcasts.podcasts[6].id}/>
                    </CarouselItem>

                    <CarouselItem>
                        <PodcastCarouselCard podcastimage={podcasts.podcasts[7].thumbnail}
                        podcasttitle={podcasts.podcasts[7].title}
                        podcastauthor={podcasts.podcasts[7].publisher}
                        podcastdescription={podcasts.podcasts[7].description}
                        podcastid={podcasts.podcasts[7].id}/>
                    </CarouselItem>

                    <CarouselItem>
                        <PodcastCarouselCard podcastimage={podcasts.podcasts[8].thumbnail}
                        podcasttitle={podcasts.podcasts[8].title}
                        podcastauthor={podcasts.podcasts[8].publisher}
                        podcastdescription={podcasts.podcasts[8].description}
                        podcastid={podcasts.podcasts[8].id}/>
                    </CarouselItem>

                    <CarouselItem>
                        <PodcastCarouselCard podcastimage={podcasts.podcasts[9].thumbnail}
                        podcasttitle={podcasts.podcasts[9].title}
                        podcastauthor={podcasts.podcasts[9].publisher}
                        podcastdescription={podcasts.podcasts[9].description}
                        podcastid={podcasts.podcasts[9].id}/>
                    </CarouselItem>
            </PodcastCarousel>

            {/* <CustomComments/> */}
        </div>
    )
}


export default Listen;
