import React from 'react'; 
import Slider from "react-slick";
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';


const Sliders = ()=>{
    const settings = {
        lazyload: true,
        dots: true,
        infinite: true,
        speed: 500,
        slidesToShow: 3,
        slidesToScroll: 1, 
        initialSlide: 2,
        autoplay: true,
        speed: 2000, 
        autoplaySpeed: 2000, 
        ccsEase: "linear"
      };

    const posts = [1, 2, 3, 4, 5, 6, 7, 8, 9];
    const style={
        width: "20rem"
        };


    return (
        <div className = "container">
            <div style = {{marginTop: "20px"}}>
                <Slider {...settings}>
                    {posts.map((index)=>{
                        return (
                            <div> 
                                <Card style={style}>
                                    <Card.Img variant="top" src="./assets/download.svg" />
                                    <Card.Body>
                                        <Card.Title>Podcast Title{index}</Card.Title>
                                        <Card.Subtitle>Author</Card.Subtitle>
                                        <Card.Text>
                                            Description text for the podcast. 
                                        </Card.Text>
                                        <Button variant="primary">Listen</Button>
                                        <Button variant="primary">Follow</Button>
                                    </Card.Body>
                                </Card>
                            </div>
                        );
                    })}      
                </Slider>
            </div>
        </div>
    );


}

export default Sliders;
