import "./modal.css";
import { useState } from "react";
import PasswordResetInitialModal from "./PasswordResetInitialModal";
import PasswordResetEmailSentModal from "./PasswordResetEmailSentModal";
import PasswordResetConfirmModal from "./PasswordResetConfirmModal";
import LoginModal from "./LoginModal";


function PasswordResetJourney({ setScreen, onClose }) {

    // const [screen, setScreen] = useState(1)
    const [passwordScreen, setPasswordScreen ] = useState(1)

    function nextScreen(screenValue){
        setPasswordScreen(current => current +1)
    };

    function closeModal(){
        setScreen('login');
        onClose();
    }; 

    return (

        <>
        { passwordScreen===1 && <PasswordResetInitialModal nextScreen={nextScreen} onClose={closeModal} />}
        { passwordScreen===2 && <PasswordResetEmailSentModal nextScreen={nextScreen} onClose={closeModal} />}
        { passwordScreen===3 && <PasswordResetConfirmModal nextScreen={nextScreen} onClose={closeModal} />}
        { passwordScreen===4 && <LoginModal onClose={closeModal}/>}
        </>
    )
};

export default PasswordResetJourney;
