import React from "react";
import "./modal.css";

const PasswordResetInitialModal = ({ nextScreen, setPasswordScreen, onClose }) => {

    return (
        <div className="main-modal-window">

            <div className="main-modal-content">

                {/* Close Icon */}
                <div className="close-container">
                    <svg onClick={onClose} version="1.1" className="close-icon" x="0px" y="0px" viewBox="0 0 47.971 47.971"><path d="M28.228,23.986L47.092,5.122c1.172-1.171,1.172-3.071,0-4.242c-1.172-1.172-3.07-1.172-4.242,0L23.986,19.744L5.121,0.88c-1.172-1.172-3.07-1.172-4.242,0c-1.172,1.171-1.172,3.071,0,4.242l18.865,18.864L0.879,42.85c-1.172,1.171-1.172,3.071,0,4.242C1.465,47.677,2.233,47.97,3,47.97s1.535-0.293,2.121-0.879l18.865-18.864L42.85,47.091c0.586,0.586,1.354,0.879,2.121,0.879s1.535-0.293,2.121-0.879c1.172-1.171,1.172-3.071,0-4.242L28.228,23.986z"/></svg>
                </div>

                {/* Header space in Modal */}
                <div className="main-modal-header">

                </div>

                {/* Main content of modal */}
                <div className="main-modal-body">

                    <h3 className="centred-modal-title">Password Reset Request</h3>
                    <p> You will receive an email to the address associated<br></br>
                        with your  account with a link to reset your password.<br></br><br></br>
                        To confirm, please enter the email address<br></br>
                        for your account below:
                    </p>

                    {/* Password Reset form */}
                    <form>
                        <input type="text" className="input-field" name="emailAddress" placeholder="Enter your email address..."/>
                    </form>

                    <button type="submit" onClick={() => setPasswordScreen(2)} className="button">Send Email</button>

                </div>

                <div className="main-modal-footer">

                </div>

            </div>

        </div>
    )
}

export default PasswordResetInitialModal;
