import './Carousel.css'; 
import React, { useEffect, useState, useRef } from 'react';
import { useSwipeable } from 'react-swipeable';

function PodcastCarousel({children}){

    const [activeIndex, setActiveIndex] = useState(0);
    const [paused, setPaused] = useState(false);

    const updateIndex = (newIndex) => {
        if (newIndex < 0) {
            newIndex = React.Children.count(children) - 1;
        } else if (newIndex >= React.Children.count(children)) {
            newIndex = 0;
        }

        setActiveIndex(newIndex);
    };

    useEffect(() => {
        const interval = setInterval(() => {
            if (!paused) {
                updateIndex(activeIndex + 1);
            }
        }, 3000);

        return () => {
            if (interval) {
                clearInterval(interval);
            }
        };
    });

    const handlers = useSwipeable ({
        onSwipedLeft: () => updateIndex(activeIndex +1),
        onSwipedRight: () => updateIndex(activeIndex - 1)
    });

    return (
        <div
            {...handlers} 
            className='podcast-carousel'
            onMouseEnter={() => setPaused(true)}
            onMouseLeave={() => setPaused(false)}
        >

            <div className='carousel-header'>
                <div className='saved-for-later-container'>
                <div className='saved-title'>
                    {/* <div className='carousel-title'>
                        Favourites of the Month
                    </div>
                    <p className='carousel-subtitle'>A spotlight on this month's favourite Podcasts from listeners</p> */}
                </div>
                </div>
            </div>
{/* 
            <div className='carousel-contents' id='container3'>

                {React.Children.map(children, (child, index) => {
                    return React.cloneElement(child, { width: "33%"});
                })}

            </div> */}

            <div className='carouselcontainer'>
            <div className='carousel-contents' style={{transform: `translateX(-${activeIndex * 100}%)`}}>
                {React.Children.map(children, (child, index) => {
                    return React.cloneElement(child, { width: "33%"});
                })}
            </div>



            <div className='podcast-carousel-indicators'>

                 <div className= "back-button-section" style={{position: 'absolute', top:'50%',left: 20,transform: 'translate(-50%,-50%)'}}> 

                 <svg className= "previous-arrow" onClick = {() => {
                    updateIndex(activeIndex - 1);
                }} version="1.1" x="0px" y="0px" viewBox="0 0 59.414 59.414"> <polygon points="45.268,1.414 43.854,0 14.146,29.707 43.854,59.414 45.268,58 16.975,29.707 "/></svg>
                </div>

                <div className="forward-button-section" style={{position: 'absolute', top:'50%',right: 20,transform: 'translate(-50%,-50%)'}}>
                    <svg className = "next-arrow" onClick = {() => {
                    updateIndex(activeIndex + 1);
                }} version="1.1" x="0px" y="0px" viewBox="0 0 59.414 59.414" > <polygon points="15.561,0 14.146,1.414 42.439,29.707 14.146,58 15.561,59.414 45.268,29.707 "/> </svg> 
                </div>
            </div>
            </div>

         </div>
    );


};

export default PodcastCarousel;
