import './GenreButton.css';
import { useState } from 'react';



function GenreButton(props) {
    const [isActive, setIsActive] = useState(false);

    const handleClick = () => {
        setIsActive(current => !current);
    };

    return (
        <>
          <button id={ props.id } className='genrebutton'
          style={{backgroundColor: isActive ? '#0B1E20' : '',
        color: isActive ? '#519a9e' : '',}}
          onClick={handleClick}>{ props.genrebuttontitle }</button>
        </>
      );
}

export default GenreButton;
