import "./modal.css";
import { useState } from "react";
import PasswordResetInitialModal from "./PasswordResetInitialModal";
import PasswordResetEmailSentModal from "./PasswordResetEmailSentModal";
import PasswordResetConfirmModal from "./PasswordResetConfirmModal";

function PodcastJourney(props) {

    const [screen,setScreen] = useState(1)
    const [PasswordResetJourneyValue, setPasswordResetJourneyValue] = useState({})

    function nextScreen(screenValue){
        setScreen(current => current +1)
        setPasswordResetJourneyValue(previous => ({...previous, ...screenValue}))
    };

    function closeModal(){
        setScreen(1);
        props.onClose();
    }; 

    if (!props.open)
        return null

    return (

        <>
        { screen===1 && <PasswordResetInitialModal nextScreen={nextScreen} onClose={closeModal} />}
        { screen===2 && <PasswordResetEmailSentModal nextScreen={nextScreen} onClose={closeModal} />}
        { screen===3 && <PasswordResetConfirmModal nextScreen={nextScreen} onClose={closeModal} />}
        </>
    )
};

export default PodcastJourney;
