import "./modal.css";
import { useState } from "react";
import LoginModal from "./LoginModal";
import AccountCreateModal from "./AccountCreationModal";
import User from "../../containers/User/User";
import PasswordResetJourney from "./PasswordResetJourney";

// screen to link all Login Modals together
// TODO: add Password Reset screens

function LoginJourney(props) {

    const [screen, setScreen] = useState('login')
    const [passwordScreen, setPasswordScreen] = useState()


    function closeModal() {
        props.onClose();
    }

    if (!props.open)
        return null

    return (
        <>
            {screen === 'login' && <LoginModal setScreen={setScreen} onClose={closeModal} />}
            {screen === 'account' && <AccountCreateModal setScreen={setScreen} />}
            {screen === 'password' && <PasswordResetJourney setScreen={setPasswordScreen} onClose={closeModal}/>}
        </>
    )
}

export default LoginJourney;
