import './GenreButton.css';
import React, { useState } from 'react';


function GenreButton(props) {
    const [isActive, setIsActive] = useState(false);

    const handleClick = () => {
        setIsActive(current => !current)
        // Need to make sure genres are valid for the api search
        props.onClick(props.genrebuttonid, isActive);
    };

    return (

          <button id={ props.id } className={`genre-button ${isActive ? `genre-button--checked` : ``}`} 
            onClick={handleClick}>

                { props.genrebuttontitle }

          </button>

      );
}

export default GenreButton;





